import React, { useState } from "react";
import Modal from "../../components/Modal";
import NumberFormat from "react-number-format";
import { FarmContract } from "../../../packages/neo/contracts";
import { useWallet } from "../../../packages/provider";
import { toast } from "react-hot-toast";
import TransactionSubmitted from "../../components/TransactionSubmitted";

interface IActionModal {
  onClose: () => void;
}
const ActionModal = ({ onClose }: IActionModal) => {
  const [amount, setAmount] = useState("");
  const [position, setPosition] = useState("red");
  const { network, connectedWallet, openWalletModal } = useWallet();
  const [txid, setTxid] = useState<string>();
  const onDeposit = async () => {
    if (connectedWallet) {
      try {
        const txid = await new FarmContract(network).deposit(
          connectedWallet,
          amount,
          position
        );
        setTxid(txid);
      } catch (e) {
        console.error(e);
        // @ts-ignore
        toast.error(e.message);
      }
    } else {
      openWalletModal();
    }
  };
  // const inputRef = React.useRef();
  // React.useEffect(() => {
  //   // @ts-ignore
  //   inputRef.current.focus();
  // }, []);
  return (
    <Modal onClose={onClose}>
      {txid ? (
        <TransactionSubmitted txid={txid} onClick={onClose} />
      ) : (
        <>
          <h1 className="title is-flex">
            {/*<img src={"/neo_512_512.svg"} />*/}
            Deposit NEO
          </h1>
          <div className="columns">
            <div className="column">
              <div className="field">
                <label className="label">Your position</label>
                <div className="select is-fullwidth">
                  <select
                    value={position}
                    onChange={(e) => setPosition(e.target.value)}
                  >
                    <option value="red">RED</option>
                    <option value="blue">BLUE</option>
                  </select>
                </div>
              </div>
            </div>
            <div className="column">
              <div className="field">
                <label className="label">Amount</label>
                <NumberFormat
                  autoFocus={true}
                  // ref={this.props, inputRef}
                  decimalScale={0}
                  inputMode="decimal"
                  className="input"
                  value={amount}
                  onValueChange={(value) => {
                    setAmount(value.value);
                  }}
                  thousandSeparator={true}
                  suffix={" NEO"}
                  allowLeadingZeros={false}
                />
              </div>
            </div>
          </div>
          <button
            onClick={onDeposit}
            disabled={!amount || parseFloat(amount) < 1}
            className="button is-button is-black"
          >
            Deposit
          </button>
        </>
      )}
    </Modal>
  );
};

export default ActionModal;
