import React from "react";
import { Link, NavLink } from "react-router-dom";
// tslint:disable-next-line:no-submodule-imports
import { FaBars, FaWallet } from "react-icons/fa";
import Logo from "./Logo";
import { useApp } from "../../common/hooks/use-app";
import { useWallet } from "../../packages/provider";
import { utils } from "../../packages/neo";
import { FARM_PATH, GALLERY_PATH } from "../../consts";
import PendingTransaction from "../../packages/ui/PendingTransaction";

const Header = () => {
  const { toggleSidebar, toggleWalletSidebar } = useApp();
  const { connectedWallet, network } = useWallet();
  return (
    <nav
      className="navbar has-shadow is-white is-fixed-top"
      role="navigation"
      aria-label="main navigation"
      style={{ height: "52px" }}
    >
      <div className="container">
        <div
          className="navbar-brand"
          style={{ justifyContent: "space-between" }}
        >
          <div
            role="button"
            // className={`navbar-burger is-center ${
            //   sidebarStatus ? "is-active" : ""
            // }`}
            className="navbar-burger is-center is-hidden-tablet"
            style={{ marginLeft: 0 }}
            onClick={toggleSidebar}
          >
            <FaBars />
          </div>
          <Link
            className="has-text-white navbar-item is-center"
            to="/"
            // style={{ margin: "auto" }}
          >
            <Logo />
          </Link>
          <div
            role="button"
            className={`navbar-burger is-center is-hidden-desktop`}
            onClick={toggleWalletSidebar}
            style={{ marginLeft: 0 }}
          >
            <FaWallet />
          </div>
        </div>
        <div className="navbar-menu ml-3">
          <div className="navbar-start">
            <NavLink
              activeClassName="is-active"
              to={GALLERY_PATH}
              className="navbar-item"
            >
              <strong>FTW NFT</strong>
            </NavLink>
            <NavLink
              activeClassName="is-active"
              to={FARM_PATH}
              className="navbar-item"
            >
              <strong>GAS</strong>
            </NavLink>
            {/*<div className="navbar-item has-dropdown is-hoverable">*/}
            {/*  <div className="navbar-link  is-arrowless">NFT</div>*/}

            {/*  <div className="navbar-dropdown">*/}
            {/*    <Link to={MINT_PATH} className="navbar-item">Mint</Link>*/}
            {/*    <Link to={GALLERY_PATH} className="navbar-item">Gallery</Link>*/}
            {/*  </div>*/}
            {/*</div>*/}
          </div>
        </div>
        <div className="navbar-end is-hidden-touch">
          <div className="navbar-item">
            <small>{network}</small>
          </div>
          <PendingTransaction />
          <div className="navbar-item">
            <div className="buttons">

	            <div className="dropdown">
                <div className="dropdown-trigger">
                  <button
                    className="button"
                    aria-haspopup="true"
                    aria-controls="dropdown-menu3"
                  >
                    <span>Click me</span>
                    <span className="icon is-small">
                      <i className="fas fa-angle-down" aria-hidden="true"></i>
                    </span>
                  </button>
                </div>
                <div className="dropdown-menu" id="dropdown-menu3" role="menu">
                  <div className="dropdown-content">
                    <a href="#" className="dropdown-item">
                      Overview
                    </a>
                    <a href="#" className="dropdown-item">
                      Modifiers
                    </a>
                    <a href="#" className="dropdown-item">
                      Grid
                    </a>
                    <a href="#" className="dropdown-item">
                      Form
                    </a>
                    <a href="#" className="dropdown-item">
                      Elements
                    </a>
                    <a href="#" className="dropdown-item">
                      Components
                    </a>
                    <a href="#" className="dropdown-item">
                      Layout
                    </a>
                    <hr className="dropdown-divider" />
                    <a href="#" className="dropdown-item">
                      Disconnect
                    </a>
                  </div>
                </div>
              </div>

              <button
                onClick={toggleWalletSidebar}
                className="button is-small is-black is-rounded"
              >
                {connectedWallet
                  ? utils.truncateAddress(connectedWallet.account.address)
                  : "Connect wallet"}
              </button>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Header;
