        {/*    /!*<Route exact path="/neofi">*!/*/}
        {/*    /!*  <NeoFi />*!/*/}
        {/*    /!*</Route>*!/*/}
        {/*    /!*<Route exact path="/migration">*!/*/}
        {/*    /!*  <Migration />*!/*/}
        {/*    /!*</Route>*!/*/}
        {/*  </Switch>*/}
        {/*</Sidebar>*/}
        <MobileMenuSlider />
        <WalletSidebar />
      </Router>
    </WalletContextProvider>
  );
};

export default App;
