import React, { useState } from "react";
import { IFarmContractStatus } from "../../../packages/neo/contracts/ftw/farm/interfaces";
import { useWallet } from "../../../packages/provider";
import { FarmContract } from "../../../packages/neo/contracts";
import { toast } from "react-hot-toast";
import Countdown from "react-countdown";
import SnapshotCountdown from "./SnapshotCountdown";

interface ISnapshotListProps {
  contractStatus?: IFarmContractStatus;
}
const SnapshotList = ({ contractStatus }: ISnapshotListProps) => {
  const { network, connectedWallet, openWalletModal } = useWallet();
  const [txid, setTxid] = useState<string>();
  const onCreateSnapshot = async () => {
    if (connectedWallet) {
      try {
        const txid = await new FarmContract(network).createSnapshot(
          connectedWallet
        );
        setTxid(txid);
      } catch (e: any) {
        console.error(e);
        toast.error(e.message);
      }
    } else {
      openWalletModal();
    }
  };
  if (!contractStatus) return <div></div>;
  return (
    <div className="box">
      <div className="level">
        <div className="level-left">
          <div className="level-item">
            <h1 className="title is-4 is-marginless">Snapshots</h1>
          </div>
        </div>
        <div className="level-right">
          <div className="level-item">
            <SnapshotCountdown
              timeLeft={parseFloat(contractStatus.timeLeft)}
              onCreateSnapshot={onCreateSnapshot}
            />
          </div>
        </div>
      </div>

      {contractStatus.snapshots.items.map((item) => {
        return (
          <div key={item.createdAt} className="media">
            <div className="media-left">#{item.no}</div>
            <div className="media-content">
              Position:{" "}
              <span className="is-capitalized">{item.winPosition}</span>
              <br />
              GAS: {item.gasBalance}
              <br />
              {item.createdAt}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default SnapshotList;
